*{margin: 0; padding: 0; font-family: 'Noto Sans KR', sans-serif;}
li{list-style: none;}
a{text-decoration: none; color: black; }

.body {position: relative;}




.page01 {
    width: 100vw; height: 100vh;
    background-color: black;
    background-size: cover;
    color: white;
    overflow: hidden;
}
#lnb {
    width:100%; height: 50px;
    background-color: black;
}
#lnb ul {
    width: 500px; height: 50px;
    float: right;
    text-align: center;
    line-height: 50px;
    margin-right:100px;
}
#lnb ul li {
    display: inline-block;
    margin-right: 20px;
    font-size: 25px;
}
#lnb ul li a {color: white;}
#lnb ul li a:hover {color: #999;}
#lnb ul li a:active {color: darkblue;}

.fixed {
    position: fixed;
    top: 0;
    z-index: 99;
}
.container {
    width: 1268px; height: 500px;
    margin: 0 auto;
    margin-top: 200px;
    margin-bottom: 120px;
    position: relative;
}
.txt {
    width: 500px;
    font-size: 100px;
    float: left;
    margin-top: 50px;
    opacity: 0;
    animation: txt .6s ease-out alternate infinite;
}

@keyframes txt {
    from {opacity: 0;}
    50% { opacity: 0.1;}
    to {opacity: 0.15;}
}

.light {
    position: absolute;
    top: 130px; left: 110px;
    animation: light .6s ease-out alternate infinite;
    opacity: 1;
    font-size: 150px;
}

@keyframes light {
    from {opacity: 1;}
    to {
        opacity: 1;
        text-shadow: 6px 6px 50px white; 
       
    }
}

.light_img {
    width: 300px; height: 400px;
    float: right;
    background-image: url(./images/light01.jpg);
    background-position: center;
    background-repeat: no-repeat;
    background-size: 250%;
}

.mouse {
    width: 100px; height: 100px; 
    margin: 0 auto;
    text-align: center;

}
.mouse_box {
    width: 30px; height: 50px;
    border: 2px solid white;
    margin: 0 auto;
    border-radius: 50px;
    margin-bottom: 5px;
}
.pin {
    width: 7px; height: 7px;
    background-color: white;
    border-radius: 50%;
    margin: 0 auto;
    margin-top: 5px;
    animation: pin 2s infinite;
}
@keyframes pin {
    from {margin-top: 5px;}
    70% { margin-top: 38px;}
    to {
        margin-top: 40px;
        opacity: 0;
    }
}
.mouse_txt {
    font-size: 18px;
}



.page02 {
    width: 100vw; height: 100vh;
    position: relative;
    overflow: hidden;
}

.intro {
    width: 40%; height: 100%;
    float: left;
    margin-left: 140px;
    position: sticky;
    top: 0; left: 0; 
}
.title {
    width: 250px;
    font-size: 50px;
    text-align: center;
    margin: 0 auto;
    margin-top: 30px;
    margin-left: 50px;
    padding-bottom: 7px;
    border-bottom: 5px solid black;
    
}
.sub_title {
    text-align: center;
    margin-top: 50px;
    font-size: 25px;
    font-weight: bold;
}
.picture {
    width: 300px; height: 400px;
    border: 1px solid black;
    margin: 0 auto;
    margin-top: 30px;
}
.detail {
    width: 300px;
    margin: 30px auto;
    line-height: 35px;
}
.detail li {
    display: inline-block;
    width: 200px; height: 30px;
    font-size: 20px;
    margin-left: 50px;
}
.detail li  img {
    width: 20px; height: 20px;
}



.about {
    width: 45%; height: 100%;
    float: left;
    margin-right: 140px;
    position: relative;
}
.skill {
    width: 350px; height: 400px;
    border: 1px solid black;
    background-color: white;
    position: absolute;
    top: 800px;
    transition: all 1s ease-in-out;
    box-shadow: 6px 6px 6px #999;
}
.about_title {
    font-size: 30px;
    text-align: center;
    margin-top: 10px;
    font-weight: bold;
}
.about_title img {
    width: 20px; height: 20px;
}
.skill_name {
    width: 300px; height: 450px;
    margin: 0 auto;
}
.skill_name li {
    width: 300px;
    display: inline-block;
    font-size: 18px;
    margin-bottom: 10px;
}
.gauge_box {
    width: 250px; height: 15px;
    border: 1px solid black;
    float: left;
}
.gb {
    width: 220px; height: 15px;
    background-color: black; 
}
.gb1 {
    width: 200px; height: 15px;
    background-color: black; 
}
.number {
    width: 30px;
    float: left;
    font-size: 15px;
    margin-left: 10px
}
.motivation {
    width: 600px; height: 450px;
    border: 1px solid black;
    background-color: white;
    position: absolute;
    top: 900px;
    transition: all 1s ease-in-out;
    box-shadow: 6px 6px 6px #999;
}
.motive {
    width:  550px; 
    margin: 0 auto;
    margin-top: 10px;
    font-size: 19px;
    text-align: center;
}
.activity {
    width: 400px; height: 190px;
    border: 1px solid black;
    font-size: 19px;
    background-color: white;
    position: absolute;
    right: 0; top: 800px;
    transition: all 1s ease-in-out;
    box-shadow: 6px 6px 6px #999;
}
.activity ul {
    margin-top: 10px;
    margin-left: 10px;
}


.page03 {
    width: 100vw; height: 100vh;
    background-image: url(./images/backgroundbanner02.jpg);
    background-repeat: no-repeat;
    background-position-y: 150px ;
    margin: 0 auto;
}
.project {
    width: 1634px;
    margin: 0 auto;
}
.title {
    text-align: left;
}
.self h1, .team h1 {
    font-size: 35px;
    text-align: center;
    margin-top: 20px;
}
.self, .team {
    width: 700px; height: 700px;
    float: left;
    border: 1px solid black;
    background-color: white;
    box-sizing: border-box;
    margin-left: 78px;
    margin-top: 50px;
    cursor: pointer;
    transition: all .3s ease-in-out;
}
.self:hover {
    transform: translateX(3px);
    box-shadow: 6px 6px 6px #999;
}
.team:hover {
    transform: translateX(3px);
    box-shadow: 6px 6px 6px #999;
}






.page04 {
    width: 100vw; height: 100vh;
    position: relative;
    overflow: hidden;
   background-image: url(./images/contact_banner02.jpg);
   background-repeat: no-repeat;
   background-position-y: 500px;
}
.thanks {
    width: 1634px;  height: 700px;
    margin: 0 auto;
}
.title01 {
    width: 220px;
    font-size: 50px;
    text-align: center;
    margin: 0 auto;
    margin-left: 50px;
    padding-bottom: 7px;
    border-bottom: 5px solid black;
}
.txt_box {
    width: 1000px; height: 500px;
    margin: 0 auto;
    text-align: center;
}
.last {
    margin-top: 50px;
    font-size: 40px;
    font-weight: 1000;
    color: black;
    animation: txt_light 3s infinite linear;
    transition: all 1s linear;
    position: absolute;
    top: -400px;
}

.thx {
    font-size: 55px;
    position: absolute;
    bottom: -400px; left: 27%;
    transition: all 1s linear;
}
.contact_me {
    width: 600px; height: 150px;
    margin: 0 auto;
    font-size: 25px;
    position: relative;
    background-color: white;
}
.animal {
    width: 200px; height: 150px;
    float: left;
}



.top {
    width: 50px; height: 50px;
    position: fixed;
    top: 90%; right: 5%;
    background-color: black;
    color: #eee;
    font-size: 18px;
    text-align: center;
    line-height: 50px;
    transition: all .3s linear;
    display: none;
}
.top:hover {
    width: 80px;
}
